import Foundation
@Observable
class CardsViewModel {
    var cards: [FlashCardModel] = [
        .init(imageUrl: "https://laulima.hawaii.edu/access/content/group/613e6b40-7843-4d5c-94ca-41a9e0831b95/CBasic1/u1/img/hao.png",
              pingYing: "OOOO",
              character: "TTTT"),
        .init(imageUrl: "data:image/jpeg;base64,",
              pingYing: "OOOO",
              character: "TTTT")
    ]
    var currentIndex: Int = 0
    
    var isValidIndex: Bool {
        cards.count > 0
    }
    
    func next() {
        guard currentIndex + 1 < cards.count else {
            return
        }
        
        currentIndex += 1
    }
    
    func perv() {
        guard currentIndex - 1 >= 0 else {
            return
        }
        
        currentIndex -= 1
    }
}
